mapper.select:
SELECT
-- 级联表的字段 ,boke.yonghu_id as bokeYonghuId ,boke.boke_name as bokeName ,boke.boke_uuid_number as bokeUuidNumber ,boke.boke_photo as bokePhoto ,boke.boke_types as bokeTypes ,boke.boke_clicknum as bokeClicknum ,boke.boke_content as bokeContent ,boke.shangxia_types as shangxiaTypes ,boke.boke_delete as bokeDelete ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_shiyong_types as yonghuShiyongTypes ,yonghu.yonghu_email as yonghuEmail
FROM boke_liuyan a left JOIN boke boke ON a.boke_id = boke.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.boke_id = #{params.bokeId} )
and ( a.yonghu_id = #{params.yonghuId} or boke.yonghu_id = #{params.yonghuId} )
and a.boke_liuyan_text like CONCAT('%',#{params.bokeLiuyanText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.boke_id IS NOT NULL
and boke.boke_name like CONCAT('%',#{params.bokeName},'%')
and boke.boke_uuid_number like CONCAT('%',#{params.bokeUuidNumber},'%')
and boke.boke_types = #{params.bokeTypes}
and boke.boke_clicknum >= #{params.bokeClicknumStart}
and boke.boke_clicknum <= #{params.bokeClicknumEnd}
and boke.boke_clicknum = #{params.bokeClicknum}
and boke.boke_content like CONCAT('%',#{params.bokeContent},'%')
and boke.shangxia_types = #{params.shangxiaTypes}
and boke.boke_delete >= #{params.bokeDeleteStart}
and boke.boke_delete <= #{params.bokeDeleteEnd}
and boke.boke_delete = #{params.bokeDelete}
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_shiyong_types = #{params.yonghuShiyongTypes}
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
order by a.${params.orderBy} desc
